import React , {useEffect,useState} from "react";
import {isEmpty, message , connectWallet} from './utils';
import {useSelector,useDispatch} from "react-redux";
import {setUpUser , setUpWeb3 , setUpRigelPool} from './store/action';
import {tokens,NETWORKS} from "./constants/index"


function App() {

  const web3 = useSelector(state => state.web3);
  const rigelpool  = useSelector(state => state.rigelpool);
  const user = useSelector(state => state.user);
  const [data , setData] = useState({token1 : '' , token2 : '' , amount1 : '' , amount2 : ''});
  const [images , setImages] = useState({img1 : '' , img2:''})


  const dispatch = useDispatch();


  const connectUser = async (e) => {
    e.preventDefault();
    if(isEmpty(web3.instance)){message("error" , "No web3 instance found");return}
    try{
      const userData = await connectWallet(web3.instance);
      if (!isEmpty(userData) && userData.length === 2) {
        dispatch(setUpUser({provider : userData[0] , address : userData[1] ,  status : "connect"}));
      }
    }catch(error){
      message("error" , error.message);
    }
  };

  const disconnectUser = async (e) => {
    e.preventDefault();
    if(isEmpty(web3.instance)){message("error" , "Please install metamask"); return}
    dispatch(setUpUser({status : "disconnect"}));
  };


  const tokenSelectHandler = (e) => {
    console.log(e.target.name);

    setData({...data, [e.target.name] : e.target.value});
    if(e.target.name === 'token1' && !isEmpty(e.target.value)){
      console.log(tokens[e.target.value].logo)
      setImages({...images,img1 : tokens[e.target.value].logo});
    }

    if(e.target.name === 'token2' && !isEmpty(e.target.value)){
      setImages({...images,img2 : tokens[e.target.value].logo});
    }

    console.log(data)
  }

  const addLiquidity = async (e) => {
     if(isEmpty(data.token1) || isEmpty(data.token2) || isEmpty(data.amount1) || isNaN(data.amount1) || isNaN(data.amount2) || isEmpty(data.amount2) ){
       message('error',"Please Enter/Select Valid Data");
       return;
     }

     if(data.token1 === data.token2 ){
      message('error',"Please Enter/Select Valid Data");
      return;
     }
     const obj = {
        _token1 : isEmpty(tokens[data.token1].address[NETWORKS["ACTIVE"].chainId]) ? tokens[data.token1].address[5777] :tokens[data.token1].address[NETWORKS["ACTIVE"].chainId],
        _token2 : isEmpty(tokens[data.token2].address[NETWORKS["ACTIVE"].chainId]) ? tokens[data.token2].address[5777] :tokens[data.token2].address[NETWORKS["ACTIVE"].chainId],
        _amount1 : data.amount1,
        _amount2 : data.amount2,
      }
     const transaction = await ((rigelpool.instance).connect(user.provider)).addLiquidity(obj, {from : user.address});
     await transaction.wait();
  }

    //web3 setup
    useEffect(() => {
      const init = async () => {
          dispatch(setUpWeb3());
          console.log()
      }
     init();
    return () => {
       console.log("unmounted web3 setup");
    }
  }, [dispatch])

    //rigelpool setup
    useEffect(()=>{
      const init = async () => {
         if(isEmpty(web3.loading) && !isEmpty(web3.instance)){
           dispatch(setUpRigelPool());
         }
      }
      init();
      return () => { }
   },[dispatch , web3])


  //user connection
  useEffect(()=>{
    const init = async () => {
      if(isEmpty(web3.loading) && !isEmpty(web3.instance)){
          console.log("mounted user connection")
          try{
            if (!isEmpty(localStorage.getItem('user'))) {
              const userData = await connectWallet(web3.instance);
              if (!isEmpty(userData) && userData.length === 2) {
                dispatch(setUpUser({provider : userData[0] , address : userData[1] ,  status : "connect"}));
              }
            }
          }catch(error){
            message("error" , error.message)
          }
        }
    }
      init();
      return () => { console.log("unmounted user connection") }
  },[dispatch , web3])


      //pool update
      useEffect(() => {
        const init = async () => {
          if(!isEmpty(data.token1) && !isEmpty(data.token2) && !isEmpty(rigelpool)){
            rigelpool.instance
          }
        }
       init();
      return () => {
         console.log("unmounted web3 setup");
      }
    }, [])


  return (
    <div className="wrapper">
    <div className="container">
      <div className="row pt-1">
        <div className="col-6">
