import React , {useEffect} from "react";
import {isEmpty, message , connectWallet} from './utils';
import {useSelector,useDispatch} from "react-redux";
import Swal from 'sweetalert2';
import {setUpUser , setUpWeb3} from './store/action'


function App() {

  const web3 = useSelector(state => state.web3);
  const rigelpool  = useSelector(state => state.rigelpool);
  const user = useSelector(state => state.user);
  const dispatch = useDispatch();


    //web3 setup
    useEffect(() => {
      const init = async () => {
          dispatch(setUpWeb3());
      }
     init();
    return () => {
       console.log("unmounted web3 setup");
    }
  }, [dispatch])




  const connectUser = async (e) => {
    e.preventDefault();
    if(isEmpty(web3.instance)){message("error" , "No web3 instance found");return}
    try{
      const userData = await connectWallet(web3.instance);
      if (!isEmpty(userData) && userData.length === 2) {
        dispatch(setUpUser({provider : userData[0] , address : userData[1] ,  status : "connect"}));
      }
    }catch(error){
      message("error" , error.message)
    }
  };

  const disconnectUser = async (e) => {
    e.preventDefault();
    if(isEmpty(web3.instance)){message("error" , "Please install metamask"); return}
    dispatch(setUpUser({status : "disconnect"}));
  };






  return (
    <div className="wrapper">
    <div className="container">
      <div className="row pt-1">
        <div className="col-6">
          <h1 className="h4">RigelPool</h1>
        </div>
        <div className="col-6 text-right p-2">
          <button onClick={connectUser} className="btn border text-light">Connect Wallet
          </button></div>
      </div>
    </div>
    <div className="container">
      <div className="row pt-5">
        <div className="box col-12  col-md-5 offset-md-4 col-lg-5 offset-lg-4 p-0 card shadow ">
          <div className="header pt-5 pl-4 pr-4 pb-2 row ">
            <div className="icon col-2 col-md-1 col-lg-1  p-2">
              <i className="fa fa-arrow-left" />
            </div>
            <div className="content col-7 col-md-8 col-lg-8   pl-4">
              <h3 >Add Liquidity</h3>
              <span >Add liquidity to receive LP tokens</span>
            </div>
            <div className="icon col-2  col-md-2 col-lg-2  p-2">
              <i className="fa fa-cog" />
            </div>
            <div className="icon col-1  col-md-1 col-lg-1  p-2">
              <i className="fa fa-refresh" />
            </div>
          </div>
          <div className="row pl-5 pr-5 pt-4">
            <div className="form-group col-md-12 col-sm-12 ">
              <div className="row">
                <div className="img col-2  col-md-2  text-center">
                  <img src="https://w7.pngwing.com/pngs/1007/775/png-transparent-bnb-cryptocurrencies-icon-thumbnail.png" alt="" />
                </div>
                <div className="col-5 col-md-5 selectBox text-center">
                  <select>
                    <option>Select a currency</option>
                    <option>BNB</option>
                  </select>
                </div>
              </div>
              <div className="input row">
                <input type="text" placeholder="0.0" className="form-control col-md-12 text-right" />
              </div>
            </div>
            <div className="form-group col-md-12 text-center  "> 
              <i className="fa fa-plus" />
            </div>
            <div className="form-group col-md-12 col-sm-12 ">
              <div className="row">
                <div className="img col-2  col-md-2  text-center">
                  <img src="https://w7.pngwing.com/pngs/1007/775/png-transparent-bnb-cryptocurrencies-icon-thumbnail.png" alt="" />
                </div>
                <div className="col-5 col-md-5 selectBox text-center">
                  <select>
                    <option>Select a currency</option>
                    <option>BNB</option>
                  </select>
                </div>
              </div>
              <div className="input row">
                <input type="text" placeholder="0.0" className="form-control col-md-12 text-right"  />
              </div>
            </div>
          </div>
          <div className="display row pl-4 pr-4 pb-1 pt-1">
            <div className="col-md-12 info pt-3">
              <h4>Prices and pool share</h4>
              <div className="detail row p-3">
                <div className="col-6 col-md-4 text-center">
                  <h4>257.525</h4>
                  <p>BELT per BNB</p>
                </div>
                <div className="col-6 col-md-4 text-center">
                  <h4>0.00388312</h4>
                  <p>BNB per BELT</p>
                </div>
                <div className="col-6 col-md-4 text-center">
                  <h4>0%</h4>
                  <p>Share of Pool</p>
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-12">
            <button className="btn  d-block mainBtn">Connect Wallet</button>
            {/* <button class="btn  d-block mainBtn">Enter an amount</button> */}
          </div>
        </div>
      </div>
    </div>
  </div>
  );
}

export default App;
